import React, { useEffect, useState, useRef } from "react";
import { BrowserRouter as Router, Routes, Route, useNavigate } from "react-router-dom";
import "./styles.css"; // 7 Star Interior styles
import "./App.css";    // One Doctor styles
// Import your other pages/components as needed:
import { Landing } from "./pages/Landing";
import Login from "./pages/Login";
import Signup from "./pages/Signup";
import Navbar from "./components/NavBar";
import { Home } from "./pages/Home";
import ProtectedRoute from "./components/ProtectedRoute";
import About from "./pages/About";
import Terms from "./pages/Terms";
import Contact from "./pages/Contact";
import SubmitHealthData from "./pages/SubmitHealthData";
import ViewHealthData from "./pages/ViewHealthData";
import MedicalHistory from "./pages/MedicalHistory";
import {
  Home as HomeIcon,
  FilePlus,
  Eye,
  FileText,
  Info,
  FileLock,
  Mail,
  Moon,
  Sun
} from "lucide-react";
// This component is the multilingual landing page of 7 Star Interior
function SevenStarLanding() {
  const [language, setLanguage] = useState("en");
  const [title, setTitle] = useState("Welcome to 7 Star Interior App");
  const [tagline, setTagline] = useState("Design with Voice. Visualize with AI. Approve with Ease.");
  const [loginLabel, setLoginLabel] = useState("Login");
  const loginLabels = {
    en: "Login",
    fr: "Connexion",
    es: "Iniciar sesión",
    hi: "लॉग इन करें",
    te: "లాగిన్",
  };
  useEffect(() => {
    translateText("Welcome to 7 Star Interior App", setTitle);
    translateText("Design with Voice. Visualize with AI. Approve with Ease.", setTagline);
    setLoginLabel(loginLabels[language] || "Login");
  }, [language]);
  const translateText = (text, setter) => {
    fetch(`/api/translate?text=${encodeURIComponent(text)}&lang=${language}`)
      .then(res => res.json())
      .then(data => setter(data.translation))
      .catch(() => setter(text));
  };
  return (
    <div className="hero">
      <img src="logo.png" alt="7 Star Interior Logo" className="logo" />
      <div>
        <label htmlFor="langSelect">Language:</label>
        <select id="langSelect" value={language} onChange={e => setLanguage(e.target.value)}>
          <option value="en">English</option>
          <option value="fr">Français</option>
          <option value="es">Español</option>
          <option value="hi">हिन्दी</option>
          <option value="te">తెలుగు</option>
        </select>
      </div>
      <div className="title">{title}</div>
      <div className="tagline">{tagline}</div>
      <button id="loginBtn" onClick={() => window.location.href='/login'}>{loginLabel}</button>
      <a href="https://chat.openai.com/" target="_blank" rel="noopener noreferrer" className="chatgpt-fab" title="ChatGPT 4o-mini">
        <svg width="64" height="64" viewBox="0 0 64 64">
          <circle cx="32" cy="32" r="32" fill="#10A37F"/>
          <text x="32" y="38" textAnchor="middle" fontSize="18" fill="white" fontFamily="Arial" fontWeight="bold">4o-mini</text>
        </svg>
      </a>
    </div>
  );
}
// Main app content: routing + dark mode + dropdown menu
function AppContent() {
  const [showDropdown, setShowDropdown] = useState(false);
  const [darkMode, setDarkMode] = useState(false);
  const dropdownRef = useRef(null);
  const navigate = useNavigate();
  const toggleDropdown = () => setShowDropdown(prev => !prev);
  const toggleDarkMode = () => setDarkMode(prev => !prev);
  useEffect(() => {
    const closeDropdown = (e) => {
      if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
        setShowDropdown(false);
      }
    };
    document.addEventListener("mousedown", closeDropdown);
    return () => document.removeEventListener("mousedown", closeDropdown);
  }, []);
  const handleNav = (path) => {
    navigate(path);
    setShowDropdown(false);
  };
  return (
    <div className={`app-container ${darkMode ? 'dark' : ''}`}>
      <Navbar />
      <div className="banner">
        <div className="menu-actions">
          <button className="menu-button" onClick={toggleDropdown}>☰ Menu</button>
          <button className="toggle-theme" onClick={toggleDarkMode}>
            {darkMode ? <Sun size={18} /> : <Moon size={18} />}
          </button>
        </div>
        {showDropdown && (
          <div className="dropdown-list" ref={dropdownRef}>
            <ul className="dropdown-menu-list">
              <li onClick={() => handleNav("/")}><HomeIcon size={16} /> 7 Star Landing</li>
              <li onClick={() => handleNav("/home")}><HomeIcon size={16} /> Home</li>
              <li onClick={() => handleNav("/submit")}><FilePlus size={16} /> Submit Data</li>
