import React, { useState } from 'react';
import './styles.css';

const loginLabels = {
  en: "Login", fr: "Connexion", es: "Iniciar sesión", hi: "लॉग इन करें", te: "లాగిన్"
};

function Home() {
  const [lang, setLang] = useState("en");
  const [title, setTitle] = useState("Welcome to 7 Star Interior App");
  const [tagline, setTagline] = useState("Design with Voice. Visualize with AI. Approve with Ease.");

  const translateHome = async (lang) => {
    setLang(lang);
    const res1 = await fetch(`/api/translate?text=Welcome%20to%207%20Star%20Interior%20App&lang=${lang}`);
    const data1 = await res1.json();
    setTitle(data1.translation);

    const res2 = await fetch(`/api/translate?text=Design%20with%20Voice.%20Visualize%20with%20AI.%20Approve%20with%20Ease.&lang=${lang}`);
    const data2 = await res2.json();
    setTagline(data2.translation);
  };

  return (
    <div className="hero">
      <img src="/logo.png" alt="7 Star Interior Logo" className="logo" />
      <label>Language:
        <select value={lang} onChange={(e) => translateHome(e.target.value)}>
          <option value="en">English</option>
          <option value="fr">Français</option>
          <option value="es">Español</option>
          <option value="hi">हिन्दी</option>
          <option value="te">తెలుగు</option>
        </select>
      </label>
      <div className="title">{title}</div>
      <div className="tagline">{tagline}</div>
      <button onClick={() => window.location.href = "/login"}>
        {loginLabels[lang]}
      </button>
    </div>
  );
}
export default Home;
