import React, { useState } from "react";
import "./App.css";

const translations = {
  en: {
    title: "Welcome to 7 Star Interior App",
    tagline: "Design with Voice. Visualize with AI. Approve with Ease.",
    login: "Login"
  },
  fr: {
    title: "Bienvenue à l'application 7 Star Interior",
    tagline: "Concevez avec la voix. Visualisez avec l'IA. Approuvez facilement.",
    login: "Connexion"
  },
  es: {
    title: "Bienvenido a la aplicación 7 Star Interior",
    tagline: "Diseña con voz. Visualiza con IA. Aprueba con facilidad.",
    login: "Iniciar sesión"
  },
  hi: {
    title: "7 स्टार इंटीरियर ऐप में आपका स्वागत है",
    tagline: "आवाज़ से डिज़ाइन करें। एआई से विज़ुअलाइज़ करें। आसानी से स्वीकृत करें।",
    login: "लॉग इन करें"
  },
  te: {
    title: "7 స్టార్ ఇంటీరియర్ యాప్‌కు స్వాగతం",
    tagline: "వాయిస్‌తో డిజైన్ చేయండి. ఏఐతో విజువలైజ్ చేయండి. సులభంగా ఆమోదించండి.",
    login: "లాగిన్"
  }
};

export default function App() {
  const [lang, setLang] = useState("en");

  const handleLangChange = (e) => {
    setLang(e.target.value);
  };

  const { title, tagline, login } = translations[lang];

  return (
    <div className="hero">
      <img src="logo.png" alt="7 Star Interior Logo" className="logo" />
      <div>
        <label htmlFor="langSelect">Language:</label>
        <select id="langSelect" value={lang} onChange={handleLangChange}>
          <option value="en">English</option>
          <option value="fr">Français</option>
          <option value="es">Español</option>
          <option value="hi">हिन्दी</option>
          <option value="te">తెలుగు</option>
        </select>
      </div>
      <div className="title">{title}</div>
      <div className="tagline">{tagline}</div>
      <button onClick={() => window.location.href='/login'}>{login}</button>

      <a href="https://chat.openai.com/" target="_blank" className="chatgpt-fab" title="ChatGPT 4o-mini">
        <svg width="64" height="64" viewBox="0 0 64 64">
          <circle cx="32" cy="32" r="32" fill="#10A37F"/>
          <text x="32" y="38" textAnchor="middle" fontSize="18" fill="white" fontFamily="Arial" fontWeight="bold">4o-mini</text>
        </svg>
      </a>
    </div>
  );
}
