import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import "./styles.css"; // Your 7 Star Interior styles
// 7 Star Interior multilingual landing page
function SevenStarLanding() {
  const [language, setLanguage] = useState("en");
  const [title, setTitle] = useState("Welcome to 7 Star Interior App");
  const [tagline, setTagline] = useState("Design with Voice. Visualize with AI. Approve with Ease.");
  const [loginLabel, setLoginLabel] = useState("Login");
  const loginLabels = {
    en: "Login",
    fr: "Connexion",
    es: "Iniciar sesión",
    hi: "लॉग इन करें",
    te: "లాగిన్",
  };
  useEffect(() => {
    translateText("Welcome to 7 Star Interior App", setTitle);
    translateText("Design with Voice. Visualize with AI. Approve with Ease.", setTagline);
    setLoginLabel(loginLabels[language] || "Login");
  }, [language]);
  const translateText = (text, setter) => {
    fetch(`/api/translate?text=${encodeURIComponent(text)}&lang=${language}`)
      .then(res => res.json())
      .then(data => setter(data.translation))
      .catch(() => setter(text));
  };
  return (
    <div className="hero">
      <img src="logo.png" alt="7 Star Interior Logo" className="logo" />
      <div>
        <label htmlFor="langSelect">Language:</label>
        <select id="langSelect" value={language} onChange={e => setLanguage(e.target.value)}>
          <option value="en">English</option>
          <option value="fr">Français</option>
          <option value="es">Español</option>
          <option value="hi">हिन्दी</option>
          <option value="te">తెలుగు</option>
        </select>
      </div>
      <div className="title">{title}</div>
      <div className="tagline">{tagline}</div>
      <button id="loginBtn" onClick={() => window.location.href='/login'}>{loginLabel}</button>
      <a href="https://chat.openai.com/" target="_blank" rel="noopener noreferrer" className="chatgpt-fab" title="ChatGPT 4o-mini">
        <svg width="64" height="64" viewBox="0 0 64 64">
          <circle cx="32" cy="32" r="32" fill="#10A37F"/>
          <text x="32" y="38" textAnchor="middle" fontSize="18" fill="white" fontFamily="Arial" fontWeight="bold">4o-mini</text>
        </svg>
      </a>
    </div>
  );
}
export default function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<SevenStarLanding />} />
      </Routes>
    </Router>
  );
}
